SELECT      c.CustomerID,     c.PracticeName,     c.DentalGroup,     cs.[catalog] AS PriceCatalog,     c.Active,     c.Prospect,     c.FirstName,     c.LastName,     CONCAT(c.FirstName,' ', c.LastName) AS FullName,     c.SalesPerson AS AccountManager,     c.Type,     c.Specialty,     c.ReferredBy,     CAST(c.DateOfFirstCase AS DATE) AS DateOfFirstCase,     CAST(c.DateOfLastCase AS DATE) AS DateOfLastCase,     c.Email,     c.OfficePhone,     c.Cell,     c.Fax,     c.Address1,     c.Address2,     c.City,     c.[State],     c.ZipCode,     c.Country,     c.BillContact,     c.BillEmail,     c.BillPhone,     c.BillAddress1,     c.BillAddress2,     c.BillCity,     c.BillState,     c.BillZipCode,     c.BillCountry,     ISNULL(s.MTDSales, 0) AS MTDSales,     ISNULL(s.LMSales, 0) AS LMSales,     ISNULL(s.YTDSales, 0) AS YTDSales,     ISNULL(s.LySales, 0) AS LySales,     ISNULL(s.LTDSales, 0) AS LTDSales FROM dbo.customers c LEFT JOIN dbo.SalesSummary s      ON c.CustomerID = s.CustomerID  LEFT JOIN [dbo].[customersettingsdefaultview] cs      ON c.CustomerID = cs.CustomerID  WHERE c.Deleted = 0;